    print(seg_list)
    print('尽如人意' in pos_words)
    for sag in seg_list:
        print(sag)
        if LAST_IS_NOT:
            if sag in pos_words:
                neg = neg + 1
                print('not pos')
            elif sag in neg_words:
                pos = pos + 1
                print('not neg')
        else:
            if sag in pos_words:
                pos = pos + 1
                print('pos')
            elif sag in neg_words:
                neg = neg + 1
                print('neg')
        if sag in not_words:
            LAST_IS_NOT = True
        else:
            LAST_IS_NOT = False
    motion = math.log((1 + pos) / (1 + neg))
